// UFC Real HTML Parser - Based on actual scraped HTML content
const html = $input.first().json.data;
const events = [];

// Method 1: Extract from data attributes in the HTML
// Look for patterns like: data-main-card="29.06.25 / 04:00 CEST"
const dataCardPattern = /data-main-card="([^"]+)"/g;
let match;

while ((match = dataCardPattern.exec(html)) !== null) {
  const dateTimeStr = match[1]; // e.g., "29.06.25 / 04:00 CEST"

  // Look for venue and location info near this match
  const matchIndex = match.index;
  const surroundingText = html.substring(matchIndex - 1000, matchIndex + 1000);

  // Extract venue name
  let venue = '';
  const venueMatch = surroundingText.match(/<h5>\s*([^<]+)\s*<\/h5>/);
  if (venueMatch) {
    venue = venueMatch[1].trim();
  }

  // Extract location
  let location = '';
  const locationMatch = surroundingText.match(/<span class="locality">([^<]+)<\/span>,\s*<span class="administrative-area">([^<]+)<\/span>[\s\S]*?<span class="country">([^<]+)<\/span>/);
  if (locationMatch) {
    const city = locationMatch[1].trim();
    const state = locationMatch[2].trim();
    const country = locationMatch[3].trim();
    location = `${city}, ${state}, ${country}`;
  }

  // Extract event title/fighters from href
  let title = 'UFC Event';
  const hrefMatch = surroundingText.match(/href="\/event\/([^"]+)"/);
  if (hrefMatch) {
    title = hrefMatch[1].replace(/-/g, ' ').toUpperCase();
  }

  events.push({
    title: title,
    dateTime: dateTimeStr,
    venue: venue || 'TBD',
    location: location || 'TBD',
    source: 'UFC',
    eventType: 'UFC Event',
    sport: 'MMA'
  });
}

// Method 2: Look for event URLs and extract from surrounding context
if (events.length === 0) {
  const eventUrlPattern = /href="\/event\/([^"]+)"/g;
  let urlMatch;

  while ((urlMatch = eventUrlPattern.exec(html)) !== null) {
    const eventSlug = urlMatch[1];
    const matchIndex = urlMatch.index;
    const surroundingText = html.substring(matchIndex - 500, matchIndex + 1500);

    // Look for date/time info
