    // Look for venue
    let venue = '';
    const venueMatch = surroundingText.match(/<h5>\s*([^<]+)\s*<\/h5>/);
    if (venueMatch) {
      venue = venueMatch[1].trim();
    }

    // Look for location
    let location = '';
    const locationMatch = surroundingText.match(/class="locality">([^<]+)<.*?class="administrative-area">([^<]+)<.*?class="country">([^<]+)</);
    if (locationMatch) {
