        location: location || 'TBD',
        source: 'UFC',
        eventType: 'UFC Event',
        sport: 'MMA'
      });
    }
  }
}

// Method 3: Simple text extraction approach
if (events.length === 0) {
  // Look for date patterns in DD.MM.YY format
  const datePattern = /(\d{2}\.\d{2}\.\d{2})\s*\/\s*(\d{2}:\d{2})\s*([A-Z]+)/g;
