}

// Add color coding based on location
const eventsWithColor = events.map(event => {
  const location = event.location.toLowerCase();
  let color;

  if (location.includes('san francisco')) {
    color = 'peacock'; // Peacock for SF
  } else if (location.includes('united states') || location.includes('canada')) {
