// Alternative Regex-based Parser for Sammy Obeid Comedy Shows
// This is a simpler approach using regex patterns

const htmlData = $input.first().json.data;

const shows = [];

// Pattern to match the show structure in the HTML
// Looking for: Location heading -> Date/Time/Venue -> Ticket Link
const showBlocks = htmlData.split(/<h2[^>]*class="[^"]*rich-text__heading[^"]*"[^>]*>/);

for (let i = 1; i < showBlocks.length; i++) {
  const block = showBlocks[i];

  // Extract location (everything until the closing h2 tag)
  const locationMatch = block.match(/^([^<]+)<\/h2>/);
  if (!locationMatch) continue;

  const location = locationMatch[1].trim();

  // Skip if this doesn't look like a location
  if (!location.includes(',') && !location.match(/\b(CA|OR|WA|NY|TX|FL|IL|NV|AZ|CO|UT|NM|ID|MT|WY|ND|SD|NE|KS|OK|AR|LA|MS|AL|TN|KY|IN|OH|MI|WI|MN|IA|MO|GA|SC|NC|VA|WV|MD|DE|NJ|PA|CT|RI|MA|VT|NH|ME|AK|HI|DC)\b/)) {
    continue;
  }

  // Extract the details section (between h3 tags)
  const detailsMatch = block.match(/<h3>([^<]+(?:<br[^>]*>[^<]+)*)<\/h3>/);
  if (!detailsMatch) continue;

  const details = detailsMatch[1].replace(/<br[^>]*>/g, '\n').trim();

  // Extract ticket link
  const ticketMatch = block.match(/<a href="([^"]+)"[^>]*class="[^"]*button--primary[^"]*"[^>]*>Get Tickets<\/a>/);
  const ticketLink = ticketMatch ? ticketMatch[1] : '';

  // Parse the details to extract date, time, and venue
  const detailLines = details.split('\n').map(line => line.trim()).filter(line => line);

  let date = '';
  let time = '';
  let venue = '';

  for (const line of detailLines) {
    // Match date patterns (Month Day or just Month Day)
    if (line.match(/^(January|February|March|April|May|June|July|August|September|October|November|December|Aug|Jul|Sep|Oct|Nov|Dec)\s+\d+/i)) {
      date = line;
    }
    // Match time patterns (includes multiple times and special formatting)
    else if (line.match(/\d+:\d+\s*(PM|AM)/i)) {
      time = line;
    }
    // Match venue (starts with @)
    else if (line.startsWith('@')) {
      venue = line.substring(1).trim();
    }
  }

  // Parse location into city and state
  const locationParts = location.split(',').map(part => part.trim());
  const city = locationParts[0] || '';
  const state = locationParts[1] || locationParts[0]; // Sometimes state might be the only part

  // Determine color based on location logic from the task
  let eventColor = 'Blueberry'; // Default for outside US

  if (state && state.match(/^(AL|AK|AZ|AR|CA|CO|CT|DE|FL|GA|HI|ID|IL|IN|IA|KS|KY|LA|ME|MD|MA|MI|MN|MS|MO|MT|NE|NV|NH|NJ|NM|NY|NC|ND|OH|OK|OR|PA|RI|SC|SD|TN|TX|UT|VT|VA|WA|WV|WI|WY|DC)$/)) {
    // It's in the US
    if (city.toLowerCase().includes('san francisco') || city.toLowerCase() === 'sf') {
      eventColor = 'Peacock'; // San Francisco
    } else {
      eventColor = 'Flamingo'; // Other US cities
    }
  }

  // Create the show object
  const show = {
    title: `${location} - Sammy Obeid Comedy Show`,
    location: location,
    city: city,
    state: state,
    date: date,
    time: time,
    venue: venue,
    ticketLink: ticketLink,
    fullDetails: details,
    artist: 'Sammy Obeid',
    eventType: 'Comedy Show',
    source: 'sammyko.com/pages/shows',
    eventColor: eventColor,
    // Format for calendar integration
    summary: `Sammy Obeid Live in ${city}`,
    description: `Comedy show by Sammy Obeid at ${venue}. ${details}`,
    url: ticketLink
  };

  shows.push(show);
}

// Fallback: if no shows found, try a more aggressive regex approach
if (shows.length === 0) {
  // Look for any pattern that might be show data
  const fallbackPattern = /([^,]+,\s*[A-Z]{2,})[^]*?August\s+\d+[^]*?\d+:\d+\s*[AP]M[^]*?@([^<\n]+)[^]*?href="([^"]+)"[^>]*>Get Tickets/gi;

  let match;
  while ((match = fallbackPattern.exec(htmlData)) !== null) {
    const location = match[1].trim();
    const venue = match[2].trim();
    const ticketLink = match[3];

    const locationParts = location.split(',').map(part => part.trim());
    const city = locationParts[0] || '';
    const state = locationParts[1] || '';

    const show = {
      title: `${location} - Sammy Obeid Comedy Show`,
      location: location,
      city: city,
      state: state,
      date: 'August (see details)',
      time: 'See details',
      venue: venue,
      ticketLink: ticketLink,
      fullDetails: `Show in ${location} at ${venue}`,
      artist: 'Sammy Obeid',
      eventType: 'Comedy Show',
      source: 'sammyko.com/pages/shows',
      eventColor: state && state.match(/^[A-Z]{2}$/) ? 
        (city.toLowerCase().includes('san francisco') ? 'Peacock' : 'Flamingo') : 'Blueberry'
    };

    shows.push(show);
  }
}

// Return the results
if (shows.length > 0) {
  return shows.map(show => ({ json: show }));
} else {
  // Return debug information if no shows found
  return [{
    json: {
      error: "No comedy shows found",
      htmlLength: htmlData.length,
      sampleData: htmlData.substring(htmlData.indexOf('Portland, OR'), 500),
      message: "Check the HTML structure or try the cheerio-based parser"
