}

// Helper function to parse various date formats
function parseEventDate(dateInput) {
  if (!dateInput) return new Date();

  try {
    console.log(`Parsing date: "${dateInput}"`);

    // UFC format with different languages: "Dom, Ago 3 / 3:00 AM CEST" or "Sun, Aug 3 / 2:00 AM BST"
    if (dateInput.includes('/') && dateInput.includes(',')) {
      const parts = dateInput.split('/');
      const datePart = parts[0].trim();
      const timePart = parts[1] ? parts[1].trim() : '12:00 PM';

      // Extract date without day name and handle different languages
      let dateOnly = datePart.replace(/^[A-Za-z]+,\s*/, '');

      // Month translation map (handle Spanish/Italian months)
      const monthMap = {
        'Ago': 'Aug',
        'Ene': 'Jan',
        'Feb': 'Feb', 
        'Mar': 'Mar',
        'Abr': 'Apr',
        'May': 'May',
        'Jun': 'Jun',
        'Jul': 'Jul',
        'Sep': 'Sep',
        'Oct': 'Oct',
        'Nov': 'Nov',
        'Dic': 'Dec',
        'S√°b': 'Sat'
      };

      // Replace Spanish/Italian month names with English
      for (let [spanish, english] of Object.entries(monthMap)) {
        dateOnly = dateOnly.replace(spanish, english);
      }

      const currentYear = new Date().getFullYear();

      // Clean up time part (remove timezone)
      const cleanTime = timePart.replace(/\s+[A-Z]{3,4}$/, '');

      // Parse date with current year
      const fullDateString = `${dateOnly} ${currentYear} ${cleanTime}`;
      console.log(`Attempting to parse: "${fullDateString}"`);

      const parsedDate = new Date(fullDateString);
      if (!isNaN(parsedDate.getTime())) {
        return parsedDate;
      }
    }

    // DJ Snake format: "02/08/25"
    if (dateInput.match(/^\d{2}\/\d{2}\/\d{2}$/)) {
      const [day, month, year] = dateInput.split('/');
      const fullYear = `20${year}`;
      const parsedDate = new Date(`${fullYear}-${month}-${day}`);
      console.log(`DJ Snake date parsed: ${parsedDate}`);
      return parsedDate;
    }

    // Sammy format: "July 30" (assuming current year)
    if (dateInput.match(/^[A-Za-z]+ \d{1,2}$/)) {
      const currentYear = new Date().getFullYear();
      const parsedDate = new Date(`${dateInput} ${currentYear}`);
      console.log(`Sammy date parsed: ${parsedDate}`);
      return parsedDate;
    }

    // Fallback: try direct parsing
    const fallbackDate = new Date(dateInput);
    if (!isNaN(fallbackDate.getTime())) {
      console.log(`Fallback date parsed: ${fallbackDate}`);
      return fallbackDate;
    }

    console.log(`Could not parse date: ${dateInput}, using current date`);
    return new Date();

  } catch (error) {
    console.log(`Error parsing date: ${dateInput}`, error);
    return new Date(); // Return current date as fallback
  }
}

// Helper function to calculate end time based on event type
function calculateEndTime(startDate, eventType) {
  const endDate = new Date(startDate);

  // Duration based on event type (in milliseconds)
  const durations = {
    'UFC Event': 4 * 60 * 60 * 1000,      // 4 hours
    'Concert': 3 * 60 * 60 * 1000,        // 3 hours  
    'Comedy Show': 2 * 60 * 60 * 1000,    // 2 hours
    'default': 2 * 60 * 60 * 1000         // 2 hours default
  };

  const duration = durations[eventType] || durations.default;
  endDate.setTime(endDate.getTime() + duration);

  return endDate;
}

