    const originalEvent = aiResponse.originalEvent || {};
    const aiEnrichment = aiResponse.enrichment || {};

    // Skip if we don't have valid event data
    if (!originalEvent.title && !originalEvent.date) {
      console.log('Skipping item - no valid event data found');
      continue;
    }

    // Standardize date format
    let standardDate = parseEventDate(originalEvent.date);
    let endDate = calculateEndTime(standardDate, originalEvent.eventType);

    // Determine color based on location
    let eventColor = determineEventColor(originalEvent.location);

    // Create full location string
    let fullLocation = '';
    if (originalEvent.venue && originalEvent.location) {
      fullLocation = `${originalEvent.venue}, ${originalEvent.location}`;
    } else if (originalEvent.venue) {
      fullLocation = originalEvent.venue;
    } else if (originalEvent.location) {
      fullLocation = originalEvent.location;
    }

    // Create standardized event object for Google Calendar
    const enrichedEvent = {
      // Google Calendar required fields
      summary: originalEvent.title || `${originalEvent.artist || 'Event'} - ${originalEvent.eventType}`,
      start: {
        dateTime: standardDate.toISOString()
      },
      end: {
        dateTime: endDate.toISOString()  
      },
      location: fullLocation,
      description: createRichDescription(originalEvent, aiEnrichment),
      colorId: eventColor === 'peacock' ? '7' : eventColor === 'flamingo' ? '4' : '1',

      // Additional metadata for processing
      title: originalEvent.title || `${originalEvent.artist || 'Event'} - ${originalEvent.eventType}`,
      date: standardDate,
      endDate: endDate,
      venue: originalEvent.venue,
      color: eventColor,
      source: originalEvent.source,
      eventType: originalEvent.eventType,
      time: originalEvent.time,
      ticketLink: originalEvent.ticketLink,
      enrichment: aiEnrichment,
      originalData: originalEvent
    };

    enrichedEvents.push(enrichedEvent);

  } catch (error) {
    console.log('Error processing item:', error);
    console.log('Item data:', JSON.stringify(item, null, 2));
  }
}

// Log processing summary
console.log(`Processed ${enrichedEvents.length} events:`);
enrichedEvents.forEach(event => {
  console.log(`- ${event.summary} (${event.color}) on ${event.date}`);
});

return enrichedEvents.map(event => ({ json: event }));
